<template>
    <div class="something-card">
        <div class="something-card__recipe">
            {{ something.recipe }}
        </div>
        <div class="something-card__img">
            <img :src="something.picture" alt="">
        </div>
        <div class="something-card__title">{{ something.name }}</div>
        <div class="something-card__composition">{{ something.composition }}</div>
        <div class="something-card__volume">{{ something.weight }} л</div>
        <div class="something-card__buttons">
            <div v-if="quantity === 0" @click="quantity++; checkPrompt()" class="something-card__choose">Выбрать</div>
            <div v-else class="something-card__quantity">
                <div v-if="showPrompt" class="something-card__quantity-prompt">

                    Нажмите <span>-</span> чтобы отменить выбор коктейля
                </div>
                <div @click="quantity--" class="something-card__quantity--minus">-</div>
                <input class="something-card__quantity--num" type="text" v-model.number="quantity">
                <div @click="quantity++" class="something-card__quantity--plus">+</div>
            </div>
