</template>

<script>


import {mapState} from "vuex"

export default {
    name: "SomethingCard",
    props: {
        something: {
            type: Object,
            default: () => {
            }
        }
    },
    data() {
        return {
            quantity: 0,
            showPrompt: false,
        }
    },
    computed: {
        ...mapState({
            promptShowed: (state) => state.promptShowed
        })

    },
    watch: {
        // чтобы не смогли ввести отрицательное значение
        quantity(val) {
            if (val < 0) {
                this.quantity = 0
            }
        }
    },

    methods: {
        checkPrompt() {
            if (!this.promptShowed) {
                this.$store.commit('setValue', {
                    key: 'promptShowed',
                    value: true
                })
                this.showPrompt = true
                setTimeout(() => {
                    this.showPrompt = false
                }, 5000)
            }
        }
    }
}
</script>

