        background-size: contain
        background-repeat: no-repeat
        background-position: center
        width: 100%
        padding-top: 100%
        position: relative
        margin-bottom: 25px

        & img
            position: absolute
            left: 0
            top: 0
            height: 100%
            width: 100%
            object-fit: contain
            object-position: center

    &__title
        margin-bottom: 20px
        font-size: 17px
        text-align: center
        line-height: 1.29

    &__composition
        margin-bottom: 10px

    &__volume
        margin-bottom: 20px

    &__composition, &__volume
        text-align: center
        color: $gray
        font-size: 13px

    &__buttons
        display: flex
        align-items: center
        justify-content: center

    &__choose
        display: flex
        align-items: center
        justify-content: center
        border-radius: 100px
        padding: 12px 24px
        color: $white
        background-color: $blue
        cursor: pointer
        user-select: none

    &__recipe
        display: none

        &-button
            cursor: pointer
            margin-left: 16px
            display: flex
            align-items: center
            justify-content: center
            border-radius: 100px
            padding: 12px 24px
            border: 1px solid $light-gray
            transition: border-color .3s
            user-select: none

            &:hover
                border-color: $blue

            & svg
                margin-right: 10px

    &__quantity
        padding: 12px 24px
        display: flex
        align-items: center
        justify-content: center
        background: #FFFFFF
        box-shadow: 2px 2px 12px rgba(0, 0, 0, 0.05)
        border-radius: 100px
        user-select: none
        position: relative

        &-prompt
            left: 0
            transform: translateX(-50%)
            background-color: #F1F4F7
            border-radius: 30px
            text-align: center
            font-size: 13px
            bottom: 110%
            padding: 8px 20px
            width: 220px
            position: absolute

            &::after
                position: absolute
                top: 95%
                right: 70px
                content: ''
                border: 10px solid transparent
                border-top: 10px solid #F1F4F7
                border-right: 10px solid #F1F4F7

            & span
                font-size: 14px
                display: inline-block
                border-radius: 50%
                background-color: $white
                width: 22px
                flex-shrink: 0
                height: 22px

        &--num
            text-align: center
            font-size: 17px
            width: 32px
            margin: 0 8px

        &--plus, &--minus
            user-select: none
            border-radius: 50%
            display: flex
            align-items: center
            justify-content: center
            width: 22px
            height: 22px
            cursor: pointer
            border: 1px solid $light-gray

</style>
